  };

  const toggleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  return (
    <SafeAreaView>
      <View style={styles.judul}>
        <Text style={{ fontSize: 40, fontWeight: "bold" ,marginTop:30,}}>Sign Up</Text>
        <Text>Create an account, itâ€™s free</Text>
      </View>

      <View style={{ marginTop: 20 }}>
        <Text style={{ marginLeft: 48, color: "blue" }}>Email</Text>
      </View>
      <View style={{ alignItems: "center", marginTop: 5 }}>
        <TextInput placeholder="" style={styles.textemail}></TextInput>
      </View>

      <View style={{ marginTop: 20 }}>
        <Text style={{ marginLeft: 48, color: "blue" }}>Username</Text>
      </View>
      <View style={{ alignItems: "center", marginTop: 5 }}>
        <TextInput placeholder="" style={styles.textemail}></TextInput>

      </View>

      <View style={{ marginTop: 20 }}>
        <Text style={{ marginLeft: 48 }}>Password</Text>
      </View>

      <View style={{ alignItems: "center", marginTop: 5 }}>
        <View style={styles.passwordContainer}>
          <TextInput
            style={styles.textpassword}
            secureTextEntry={!showPassword}
            value={password}
            onChangeText={setPassword}
          />
          <TouchableOpacity style={styles.eyeIcon} onPress={toggleShowPassword}>
            <FontAwesome5 name={showPassword ? "eye-slash" : "eye"} size={18} color={"black"} />
          </TouchableOpacity>
        </View>
      </View>

      <View style={{ alignItems: "center" }}>
        <TouchableOpacity style={styles.button}  onPress={() => navigation.navigate('SignIn')}>
          <Text style={styles.buttonText}>Sign Up</Text>
        </TouchableOpacity>
      </View>

      <View style={styles.bawah}>
        <Text >Already have an account? </Text>
        <TouchableOpacity  onPress={() => navigation.navigate('SignIn')}>
          <Text style={{ color: "blue" }}>SignIn</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  judul: {
    alignItems: "center",
  },

  textemail: {
    borderWidth: 1,
    color: "blue",
    width: 300,
    height: 50,
    padding: 5,
    borderRadius: 10,
  },

  textpassword:{
    flex: 1,
    width: 260,
    height: 50,
    padding: 5,
    borderRadius: 10,
  },

  passwordContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    width: 300,
    height: 50,
    marginTop: 5,
    borderWidth: 1,
    borderRadius: 10,
    padding: 5,
    
    
  },
  eyeIcon: {
    marginRight: 5,
  },
  button: {
    backgroundColor: "#31308B",
    marginTop: 80,
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 300,
    width: 150,
  },
  buttonText: {
    color: "#fff",
    fontSize: 16,
    fontWeight: "bold",
    textAlign: "center",
  },

  bawah: {
    alignItems: "center",
    marginTop: 20,
    flexDirection: "row",
    justifyContent: "center"
  },
});

export default SignUp;
