import React, { useRef, useEffect, useState } from "react";
import { View, Text, StyleSheet, TouchableOpacity } from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";

const OrderCard = ({ title, description, price, imageUrl, onPress }) => {
  const [cardHeight, setCardHeight] = useState(0);
  const cardContentRef = useRef(null);

  useEffect(() => {
    if (cardContentRef.current) {
      cardContentRef.current.measure((x, y, width, height) => {
        setCardHeight(height + 20); // Add extra padding for better visual
      });
    }
  }, []);

  return (
    <TouchableOpacity style={[styles.cardContainer, { height: cardHeight }]} onPress={onPress} ref={cardContentRef}>
      <View style={styles.cardContent}>
        <View style={styles.imageContainer}>
          <Icon name="shopping-cart" size={30} color="#1C0235" />
        </View>
        <Text style={styles.title}>{title}</Text>
        <Text style={styles.description}>{description}</Text>
        <Text style={styles.price}>{price}</Text>
        <TouchableOpacity style={styles.addButton} onPress={onPress}>
          <Text style={styles.addButtonText}>+ </Text>
        </TouchableOpacity>
      </View>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  cardContainer: {
    flexDirection: "row",
    alignItems: "center",
    padding: 10,
    backgroundColor: "#F0F0F0",
    borderRadius: 10,
    borderWidth: 1,
    borderColor: "#1C0235",
    marginBottom: 10,
  },
  cardContent: {
    flex: 1,
    flexDirection: "column",
  },
  imageContainer: {
    alignSelf: "flex-start", // Align the image to the start of the container
    marginBottom: 10,
  },
  title: {
    fontSize: 18,
    fontWeight: "bold",
    color: "#1C0235",
  },
  description: {
    fontSize: 14,
    color: "#888",
  },
  price: {
    fontSize: 18,
    fontWeight: "bold",
    color: "#DB1717",
  },
  addButton: {
    backgroundColor: "#DB1717",
    paddingVertical: 10,
    paddingHorizontal: 12,
    borderRadius: 5,
    width: 40,
    marginTop: 5,
  },
  addButtonText: {
    color: "#fff",
    fontSize: 18,
    fontWeight: "bold",
    textAlign: "center",
  },
});

export default OrderCard;
